import { useState, useEffect} from "react";
import { Link } from "react-router-dom";
import "./ProductList.scss"

const ProductList = () => {
const [products,setProducts] = useState ([]);
useEffect (()=> {
    fetchData ();
},[]);

const fetchData = async () => {
    const response = await fetchData('http://localhost:8181/products');
    const data = await response.json();
    setProducts (data);
}

const deleteProduct = async (id) => {
    await fetch ('http://localhost:8181/products/${id}', {
        method: "Delete",
        Headers: {
            'Content-Type':'application/json'
        }
    });
    fetchData();
}
    return(
        <div>
            <Link to ="/add" className="button is-primare my-5">Add new</Link>
            <ul>
                 { products.map((product) => (
                    <li key = {product.id }> {product.title} - {product.price}
                        <button onClick={() => deleteProduct (product.id) }> Delete </button>
                    </li>
                 )
                 )}
            </ul>
        </div>
    )
}

export default ProductList;
