import React, { useState } from "react";

function App() {
  const [objects, setObjects] = useState([
    { id: 1, name: "Object 1", description: "Description 1" },
    { id: 2, name: "Object 2", description: "Description 2" },
    { id: 3, name: "Object 3", description: "Description 3" },
  ]);
  const [editingId, setEditingId] = useState(null);

  const handleCreate = ({ name, description }) => {
    const newObject = { id: objects.length + 1, name, description };
    setObjects([...objects, newObject]);
  };

  const handleUpdate = ({ id, name, description }) => {
    const updatedObjects = objects.map((object) =>
      object.id === id ? { ...object, name, description } : object
    );
    setObjects(updatedObjects);
    setEditingId(null);
  };

  const handleDelete = (object) => {
    const updatedObjects = objects.filter((o) => o.id !== object.id);
    setObjects(updatedObjects);
  };

  const handleEdit = (object) => {
    setEditingId(object.id);
  };

  const createForm = (
    <form onSubmit={handleCreate} className="form">
      <div className="form-group">
        <label htmlFor="name">Name:</label>
        <input type="text" name="name" required />
      </div>
      <div className="form-group">
        <label htmlFor="description">Description:</label>
        <textarea name="description" required />
      </div>
      <button type="submit" className="btn btn-primary">
        Create
      </button>
    </form>
  );

  const editForm = ({ id, name, description }) => (
    <form onSubmit={() => handleUpdate({ id, name, description })} className="form">
      <div className="form-group">
        <label htmlFor="name">Name:</label>
        <input type="text" name="name" defaultValue={name} required />
      </div>
      <div className="form-group">
        <label htmlFor="description">Description:</label>
        <textarea name="description" defaultValue={description} required />
      </div>
      <button type="submit" className="btn btn-primary">
        Update
      </button>
      <button type="button" onClick={() => setEditingId(null)} className="btn btn-secondary">
        Cancel
      </button>
    </form>
  );

  return (
    <div className="container">
      <h1 className="title">Objects</h1>
      {createForm}
      <ul className="list">
        {objects.map((object) => (
          <li key={object.id} className="list-item">
            {editingId === object.id ? (
              editForm(object)
            ) : (
              <>
                <h3>{object.name}</h3>
                <p>{object.description}</p>
                <button onClick={() => handleDelete(object)} className="btn btn-danger">
                  Delete
                </button>
                <button onClick={() => handleEdit(object)} className="btn btn-secondary">
                  Edit
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
