import React, { useState } from "react";
import "./App.css";

function App() {
  const [objects, setObjects] = useState([
    { id: 1, name: "Object 1", description: "Description 1" },
    { id: 2, name: "Object 2", description: "Description 2" },
    { id: 3, name: "Object 3", description: "Description 3" },
  ]);
  const [newObject, setNewObject] = useState({ name: "", description: "" });

  const handleCreate = (e) => {
    e.preventDefault();
    const newId = objects.length + 1;
    const newObjectWithId = { ...newObject, id: newId };
    setObjects([...objects, newObjectWithId]);
    setNewObject({ name: "", description: "" });
  };

  const handleDelete = (id) => {
    const updatedObjects = objects.filter((object) => object.id !== id);
    setObjects(updatedObjects);
  };

  return (
    <div className="container">
      <h1 className="title">Objects</h1>
      <form onSubmit={handleCreate} className="form">
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            value={newObject.name}
            onChange={(e) => setNewObject({ ...newObject, name: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            name="description"
            value={newObject.description}
            onChange={(e) => setNewObject({ ...newObject, description: e.target.value })}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Create
        </button>
      </form>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {objects.map((object) => (
            <tr key={object.id}>
              <td>{object.name}</td>
              <td>{object.description}</td>
              <td>
                <button onClick={() => handleDelete(object.id)} className="btn btn-danger">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
